DROP TABLE IF EXISTS `i_travelproduct`;
CREATE TABLE `i_travelproduct`  (
  `tp_id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `tp_name` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '名称',
