  `tp_site_id` int(255) NULL DEFAULT NULL COMMENT '位置',
  `tp_price` double(10, 2) NULL DEFAULT NULL COMMENT '价格',
  `tp_travelcategory_id` int(11) NULL DEFAULT NULL COMMENT '1为跟团游，2为自由行',
  `tp_number` int(11) NULL DEFAULT NULL COMMENT '数量',
  `tp_addtime` timestamp(0) NULL DEFAULT CURRENT_TIMESTAMP(0) COMMENT '添加时间',
  `tp_detail_id` int(11) NULL DEFAULT NULL COMMENT '关联商品详情id便于删除',
  `tp_state` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '状态',
  `tp_title` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '标题简介',
  PRIMARY KEY (`tp_id`) USING BTREE,
  INDEX `tp_travelcategory_id`(`tp_travelcategory_id`) USING BTREE,
  INDEX `tp_detail_id`(`tp_detail_id`) USING BTREE,
  INDEX `tp_site_id`(`tp_site_id`) USING BTREE,
  CONSTRAINT `i_travelproduct_ibfk_1` FOREIGN KEY (`tp_travelcategory_id`) REFERENCES `i_travelcategory` (`tc_id`) ON DELETE RESTRICT ON UPDATE RESTRICT,
  CONSTRAINT `i_travelproduct_ibfk_2` FOREIGN KEY (`tp_detail_id`) REFERENCES `i_traveldetails` (`td_id`) ON DELETE RESTRICT ON UPDATE RESTRICT,
  CONSTRAINT `i_travelproduct_ibfk_3` FOREIGN KEY (`tp_site_id`) REFERENCES `i_keycity` (`kc_id`) ON DELETE RESTRICT ON UPDATE RESTRICT
) ENGINE = InnoDB AUTO_INCREMENT = 63 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci ROW_FORMAT = Dynamic;

